import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './HomePage.css'; // Import custom CSS for flip effect

const events = [
  {
    name: 'Garba Night',
    date: 'Sept 17',
    location: 'Mumbai',
    image: 'https://images.unsplash.com/photo-1516450360452-9312f5e86fc7?auto=format&fit=crop&w=600&q=80',
  },
  {
    name: 'Music Festival',
    date: 'Oct 5',
    location: 'Bangalore',
    image: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=600&q=80',
  },
  {
    name: 'Comedy Club',
    date: 'Oct 20',
    location: 'Delhi',
    image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=600&q=80',
  },
];

const whyFastPass = [
  { icon: 'ðŸ“ˆ', label: 'Dynamic Price', desc: 'Get the best resale price based on demand.' },
  { icon: 'âœ…', label: 'Verified', desc: '100% real tickets, no scams.' },
  { icon: 'âš¡', label: 'Instant', desc: 'Get your ticket in seconds.' },
  { icon: 'â‚¹', label: 'Low Fees', desc: 'Lowest transaction fees in the market.' },
  { icon: 'ðŸ‡®ðŸ‡³', label: 'India-Ready', desc: 'We support UPI, Paytm, and more.' }
];

// --- Helper Components ---
const TabNav: React.FC<{ active: string, setActive: (tab: string) => void }> = ({ active, setActive }) => (
  <div className="flex border-b border-[#E5E5E5] mb-8">
    {['Buy Tickets', 'Resell Tickets', 'Auction Tickets'].map(tab => (
      <button
        key={tab}
        className={`px-6 py-3 text-base font-semibold transition-colors ${
          active === tab
            ? 'border-b-2 border-[#D6A77A] text-[#222] bg-white'
            : 'text-[#A9A9A9] bg-transparent'
        }`}
        style={{ borderRadius: '12px 12px 0 0' }}
        onClick={() => setActive(tab)}
      >
        {tab}
      </button>
    ))}
  </div>
);

const InfoBadge: React.FC<{ icon: React.ReactNode; title: React.ReactNode; desc: React.ReactNode }> = ({ icon, title, desc }) => (
  <div className="flex items-start space-x-4">
    <div className="flex-shrink-0 w-10 h-10 rounded-lg bg-[#F5E7D6] flex items-center justify-center text-2xl">{icon}</div>
    <div>
      <div className="font-bold text-[#222] mb-1">{title}</div>
      <div className="text-[#6B6B6B] text-sm">{desc}</div>
    </div>
  </div>
);

const TestimonialCard: React.FC<{ name: string; city: string; rating: number; text: string; img?: string }> = ({
  name, city, rating, text, img
}) => (
  <div className="bg-white rounded-2xl shadow p-6 flex flex-col items-start w-full max-w-md">
    <div className="flex items-center mb-3">
      <div className="w-12 h-12 rounded-full bg-[#F5E7D6] flex items-center justify-center text-xl font-bold text-[#D6A77A] mr-3">
        {img ? <img src={img} alt={name} className="w-12 h-12 rounded-full" /> : name[0]}
      </div>
      <div>
        <div className="font-bold text-[#222]">{name}</div>
        <div className="text-xs text-[#A9A9A9]">{city}</div>
      </div>
    </div>
    <div className="flex items-center mb-2">
      <span className="text-[#D6A77A] text-lg mr-1">
        {'â˜…'.repeat(Math.floor(rating))}
        {rating % 1 ? 'Â½' : ''}
      </span>
      <span className="text-xs text-[#A9A9A9] ml-1">{rating}/5</span>
    </div>
    <div className="text-[#444] text-sm">{text}</div>
  </div>
);

// --- Main Home Page ---

const HomePage: React.FC = () => {
  const [hovered, setHovered] = useState<number | null>(null);
  const [tab, setTab] = useState('Buy Tickets');

  return (
    <div className="bg-[#FAF8F6] min-h-screen font-sans">
      {/* Hero Section */}
      <section className="relative w-full h-[420px] md:h-[520px] lg:h-[600px] flex items-center justify-center overflow-hidden">
        <img
          src="/concert-hero.png"
          alt="Concert Crowd"
          className="absolute inset-0 w-full h-full object-cover"
          style={{ zIndex: 1 }}
        />
        <div className="relative z-10 flex flex-col items-center justify-center w-full h-full px-4 py-12 bg-black/40">
          <h1 className="text-3xl md:text-4xl lg:text-5xl font-extrabold text-white text-center mb-4 drop-shadow">
            India's Most Secure Ticket Resale Marketplace
          </h1>
          <p className="text-lg md:text-xl text-white text-center mb-8 max-w-2xl drop-shadow">
            Buy and sell tickets with confidence. Fastpass ensures a safe, modern, and user-friendly experience for all live events.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link to="/events" className="px-8 py-3 rounded-full font-bold bg-[#D6A77A] text-white shadow hover:bg-[#b98a5e] transition text-lg">Browse Events</Link>
            <Link to="/sell-ticket" className="px-8 py-3 rounded-full font-bold bg-white text-[#222] border border-[#E5E5E5] hover:bg-[#F5E7D6] transition text-lg">List Your Tickets</Link>
          </div>
        </div>
      </section>

      {/* How It Works */}
      <section className="w-full max-w-5xl mx-auto py-12 px-4">
        <h2 className="text-2xl md:text-3xl font-extrabold text-[#222] mb-8">How It Works</h2>
        <TabNav active={tab} setActive={setTab} />
        <div>
          {tab === 'Buy Tickets' && (
            <div className="flex flex-col md:flex-row gap-6">
              <div className="flex-1 bg-white rounded-2xl shadow p-6 flex flex-col items-start">
                <div className="font-bold text-[#222] mb-2">Browse & Discover</div>
                <div className="text-[#6B6B6B] text-sm">
                  Find tickets to your favorite events with ease. Our platform offers a wide selection of verified tickets, ensuring a smooth and secure purchase process.
                </div>
              </div>
              <div className="flex-1 bg-white rounded-2xl shadow p-6 flex flex-col items-start">
                <div className="font-bold text-[#222] mb-2">Secure Purchase</div>
                <div className="text-[#6B6B6B] text-sm">
                  Got extra tickets? Resell them securely on Fastpass. Our platform connects you with buyers, ensuring a hassle-free and safe transaction.
                </div>
              </div>
              <div className="flex-1 bg-white rounded-2xl shadow p-6 flex flex-col items-start">
                <div className="font-bold text-[#222] mb-2">Enjoy the Event</div>
                <div className="text-[#6B6B6B] text-sm">
                  For high-demand events, participate in our auctions. Bid on tickets and secure your spot at the hottest shows.
                </div>
              </div>
            </div>
          )}
          {/* You can add content for the other tabs here if needed */}
        </div>
      </section>

      {/* Safety Section */}
      <section className="w-full py-12 px-0 bg-[#F5E7D6]">
        <div className="max-w-5xl mx-auto px-4">
          <h2 className="text-2xl md:text-3xl font-extrabold text-[#222] mb-8">Your Safety is Our Priority</h2>
          <div className="flex flex-col md:flex-row gap-8">
            <InfoBadge
              icon={<svg width="24" height="24" fill="none" stroke="#D6A77A" strokeWidth="2" viewBox="0 0 24 24"><path d="M12 22s8-4 8-10V5a2 2 0 0 0-2-2H6a2 2 0 0 0-2 2v7c0 6 8 10 8 10z"/><path d="M9 11l2 2 4-4"/></svg>}
              title={<span className="text-lg md:text-xl font-bold">Verified Sellers</span>}
              desc={<span className="text-base md:text-lg">All sellers are thoroughly vetted to ensure authenticity and prevent fraud, giving you peace of mind.</span>}
            />
            <InfoBadge
              icon={<svg width="24" height="24" fill="none" stroke="#D6A77A" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="3" width="18" height="18" rx="2"/><path d="M7 7h.01M17 7h.01M7 17h.01M17 17h.01M12 12h.01"/></svg>}
              title={<span className="text-lg md:text-xl font-bold">QR-Gated Access</span>}
              desc={<span className="text-base md:text-lg">Tickets are delivered as secure QR codes, eliminating the risk of counterfeit tickets and ensuring smooth entry.</span>}
            />
            <InfoBadge
              icon={<svg width="24" height="24" fill="none" stroke="#D6A77A" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10"/><path d="M12 6v6l4 2"/></svg>}
              title={<span className="text-lg md:text-xl font-bold">Delayed Ticket Release</span>}
              desc={<span className="text-base md:text-lg">To enhance security, tickets are released closer to the event date, reducing risks associated with early transfers.</span>}
            />
          </div>
        </div>
      </section>

      {/* Testimonials */}
      <section className="w-full max-w-5xl mx-auto py-12 px-4">
        <h2 className="text-2xl md:text-3xl font-extrabold text-[#222] mb-8">Loved by Users</h2>
        <div className="flex flex-col md:flex-row gap-8">
          <TestimonialCard
            name="Priya Sharma"
            city="Mumbai, India"
            rating={5}
            text="Fastpass made buying tickets so easy and secure. I was able to get tickets to a sold-out concert without any worries! Definitely my go-to platform now."
          />
          <TestimonialCard
            name="Arjun Kapoor"
            city="Bangalore, India"
            rating={4.5}
            text="I resold my extra tickets on Fastpass and the process was seamless. Listing was quick, and I found a buyer in no time. Highly recommend!"
          />
        </div>
      </section>

      {/* Guarantee Banner */}
      <section className="w-full py-10 px-4 bg-[#F5E7D6] flex flex-col items-center justify-center">
        <div className="max-w-3xl w-full flex flex-col items-center">
          <h2 className="text-2xl md:text-3xl font-extrabold text-[#222] mb-2 text-center">Our Unwavering Guarantee</h2>
          <p className="text-[#6B6B6B] text-center mb-6">
            We stand by the authenticity of every ticket. Our dedicated support team is always ready to assist you, ensuring a worry-free experience from purchase to event day.
          </p>
          <Link to="/events" className="px-8 py-3 rounded-full font-bold bg-[#D6A77A] text-white shadow hover:bg-[#b98a5e] transition text-lg">Explore Events Now</Link>
        </div>
      </section>

      {/* Footer */}
      <footer className="w-full bg-white border-t border-[#E5E5E5] py-8">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-4">
          <div className="flex items-center space-x-2 mb-4 md:mb-0">
            <span className="w-7 h-7 rounded-lg bg-[#D6A77A] flex items-center justify-center font-extrabold text-white text-lg">L</span>
            <span className="font-extrabold text-lg text-[#222] tracking-tight">Fastpass</span>
          </div>
          <div className="flex items-center space-x-6">
            <Link to="/about" className="text-[#6B6B6B] text-sm hover:text-[#D6A77A]">About Us</Link>
            <Link to="/contact" className="text-[#6B6B6B] text-sm hover:text-[#D6A77A]">Contact</Link>
            <Link to="/terms" className="text-[#6B6B6B] text-sm hover:text-[#D6A77A]">Terms of Service</Link>
            <Link to="/privacy" className="text-[#6B6B6B] text-sm hover:text-[#D6A77A]">Privacy Policy</Link>
            <Link to="/faq" className="text-[#6B6B6B] text-sm hover:text-[#D6A77A]">FAQ</Link>
          </div>
        </div>
        <div className="text-center text-xs text-[#A9A9A9] mt-4">
          Â© 2024 Fastpass India. All rights reserved. Your trusted partner for event tickets.
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
