import React, { useState, useEffect, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext.tsx';
import LoadingSpinner from '../components/common/LoadingSpinner.tsx';
import { CalendarIcon, MapPinIcon, CurrencyDollarIcon, TicketIcon, QrCodeIcon, EyeIcon, CheckIcon, XMarkIcon, TrashIcon } from '@heroicons/react/24/outline';
import { format } from 'date-fns';
import toast from 'react-hot-toast';
import { Dialog, Transition } from '@headlessui/react';
import { ticketsAPI, bidsAPI } from '../services/api.ts';
import { Ticket as BaseTicket } from '../types/index.ts';

interface PurchasedTicket extends BaseTicket {
  purchasedAt: string;
  qrCode?: string;
}

interface ListedTicket extends BaseTicket {
  createdAt: string;
  _count?: {
    bids: number;
  };
}

interface Bid {
  id: string;
  amount: number;
  status: 'PENDING' | 'ACCEPTED' | 'REJECTED';
  createdAt: string;
  bidder: {
    id: string;
    name: string;
    email: string;
  };
}

const MyTicketsPage: React.FC = () => {
  const { user } = useAuth();
  const [tickets, setTickets] = useState<Ticket[]>([]);
  const [listings, setListings] = useState<Ticket[]>([]);
  const [loading, setLoading] = useState(true);
  const [listingsLoading, setListingsLoading] = useState(true);
  const [filter, setFilter] = useState<'all' | 'upcoming' | 'past'>('all');
  const [activeTab, setActiveTab] = useState<'purchased' | 'listed'>('purchased');
  const [resellModalOpen, setResellModalOpen] = useState(false);
  const [resellTicket, setResellTicket] = useState<Ticket | null>(null);
  const [resellPrice, setResellPrice] = useState('');
  const [resellType, setResellType] = useState<'DIRECT_SALE' | 'AUCTION'>('DIRECT_SALE');
  const [resellLoading, setResellLoading] = useState(false);

  // Bid viewing state
  const [selectedTicketBids, setSelectedTicketBids] = useState<Bid[]>([]);
  const [isBidsModalOpen, setIsBidsModalOpen] = useState(false);
  const [processingOffer, setProcessingOffer] = useState<string | null>(null);
  const [cancellingListing, setCancellingListing] = useState<string | null>(null);
  const [cancelConfirmation, setCancelConfirmation] = useState<{ ticketId: string; eventTitle: string } | null>(null);

  useEffect(() => {
    fetchMyTickets();
    fetchMyListings();
  }, []);

  const fetchMyTickets = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/tickets/my', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch tickets');
      }

      const data = await response.json();
      setTickets(data.data || []);
    } catch (error: any) {
      toast.error(error.message || 'Failed to fetch tickets');
    } finally {
      setLoading(false);
    }
  };

  const fetchMyListings = async () => {
    try {
      const response = await ticketsAPI.getMyListings();
      if (response.success) {
        setListings(response.data || []);
      } else {
        toast.error(response.message || 'Failed to fetch listings');
      }
    } catch (error) {
      toast.error('An error occurred while fetching your listings.');
    } finally {
      setListingsLoading(false);
    }
  };

  const handleViewBids = async (ticketId: string) => {
    try {
      const response = await bidsAPI.getAuctionStatus(ticketId);
      if (response.success) {
        setSelectedTicketBids(response.data || []);
        setIsBidsModalOpen(true);
      } else {
        toast.error(response.message || 'Failed to fetch bids');
      }
    } catch (error) {
      toast.error('An error occurred while fetching bids.');
    }
  };

  const handleAcceptOffer = async (bidId: string) => {
    setProcessingOffer(bidId);
    try {
      const response = await bidsAPI.acceptOffer(bidId);
      if (response.success) {
        toast.success('Offer accepted successfully! The buyer has been notified and can now view the ticket in their "My Tickets" section.');
        setIsBidsModalOpen(false);
        fetchMyListings(); // Refresh listings
      } else {
        toast.error(response.message || 'Failed to accept offer');
      }
    } catch (error) {
      toast.error('An error occurred while accepting the offer.');
    } finally {
      setProcessingOffer(null);
    }
  };

  const handleRejectOffer = async (bidId: string) => {
    setProcessingOffer(bidId);
    try {
      const response = await bidsAPI.rejectOffer(bidId);
      if (response.success) {
        toast.success('Offer rejected successfully!');
        // Update the local state to reflect the change
        setSelectedTicketBids(prev => 
          prev.map(bid => 
            bid.id === bidId ? { ...bid, status: 'REJECTED' as const } : bid
          )
        );
      } else {
        toast.error(response.message || 'Failed to reject offer');
      }
    } catch (error) {
      toast.error('An error occurred while rejecting the offer.');
    } finally {
      setProcessingOffer(null);
    }
  };

  const handleCancelListing = async (ticketId: string) => {
    setCancellingListing(ticketId);
    try {
      const response = await fetch(`http://localhost:3001/api/tickets/${ticketId}/cancel-listing`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if (response.ok) {
        toast.success('Listing cancelled successfully!');
        setCancelConfirmation(null);
        fetchMyListings(); // Refresh listings
      } else {
        toast.error(data.error || 'Failed to cancel listing');
      }
    } catch (error) {
      toast.error('An error occurred while cancelling the listing.');
    } finally {
      setCancellingListing(null);
    }
  };

  const getFilteredTickets = () => {
    const now = new Date();
    switch (filter) {
      case 'upcoming':
        return tickets.filter(ticket => new Date(ticket.event.date) > now);
      case 'past':
        return tickets.filter(ticket => new Date(ticket.event.date) <= now);
      default:
        return tickets;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'SOLD':
        return 'bg-green-100 text-green-800';
      case 'RESERVED':
        return 'bg-yellow-100 text-yellow-800';
      case 'EXPIRED':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getDisplayStatus = (status: string) => {
    switch (status) {
      case 'SOLD':
        return 'Purchased';
      case 'RESERVED':
        return 'Reserved';
      case 'EXPIRED':
        return 'Expired';
      case 'AVAILABLE':
        return 'Available';
      default:
        return status;
    }
  };

  const getEventStatus = (eventDate: string) => {
    const now = new Date();
    const eventDateObj = new Date(eventDate);

    if (eventDateObj > now) {
      const diffTime = eventDateObj.getTime() - now.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

      if (diffDays === 1) {
        return { status: 'Tomorrow', color: 'text-blue-600' };
      } else if (diffDays <= 7) {
        return { status: `In ${diffDays} days`, color: 'text-blue-600' };
      } else {
        return { status: 'Upcoming', color: 'text-green-600' };
      }
    } else {
      return { status: 'Past', color: 'text-gray-500' };
    }
  };

  const isEventWithin24Hours = (eventDate: string) => {
    const now = new Date();
    const eventDateObj = new Date(eventDate);
    const diffTime = eventDateObj.getTime() - now.getTime();
    const diffHours = diffTime / (1000 * 60 * 60);

    // Event is within 24 hours if it's less than 24 hours away and not past
    return diffHours <= 24 && diffHours >= 0;
  };

  const getQRCodeMessage = (eventDate: string) => {
    const now = new Date();
    const eventDateObj = new Date(eventDate);
    const diffTime = eventDateObj.getTime() - now.getTime();
    const diffHours = diffTime / (1000 * 60 * 60);

    if (diffHours < 0) {
      return 'Event has passed';
    } else if (diffHours <= 24) {
      return 'Show QR Code';
    } else {
      const diffDays = Math.ceil(diffHours / 24);
      return `QR available in ${diffDays} day${diffDays !== 1 ? 's' : ''}`;
    }
  };

  const openResellModal = (ticket: Ticket) => {
    setResellTicket(ticket);
    setResellPrice(ticket.price.toString());
    setResellType(ticket.listingType as 'DIRECT_SALE' | 'AUCTION');
    setResellModalOpen(true);
  };

  const closeResellModal = () => {
    setResellModalOpen(false);
    setResellTicket(null);
    setResellPrice('');
    setResellType('DIRECT_SALE');
  };

  const handleResell = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!resellTicket) return;
    setResellLoading(true);
    try {
      const response = await fetch(`http://localhost:3001/api/tickets/${resellTicket.id}/resell`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          price: parseFloat(resellPrice),
          listingType: resellType,
        }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to relist ticket');
      }
      toast.success('Ticket relisted for sale!');
      closeResellModal();
      fetchMyTickets();
      if ((window as any).refreshNavbarTicketCount) {
        (window as any).refreshNavbarTicketCount();
      }
    } catch (error: any) {
      toast.error(error.message || 'Failed to relist ticket');
    } finally {
      setResellLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-[#18122B] to-[#231651]">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  const filteredTickets = getFilteredTickets();

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#18122B] to-[#231651] py-12 px-4 sm:px-6 lg:px-8 font-sans">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-10">
          <h1 className="text-4xl font-extrabold text-neon-pink mb-2 uppercase tracking-tight">My Tickets</h1>
          <p className="text-lg text-white font-medium">View and manage your purchased tickets</p>
        </div>

        {/* Filter Tabs */}
        <div className="mb-8">
          <div className="flex space-x-4">
            <button
              onClick={() => setFilter('all')}
              className={`px-6 py-2 rounded-full text-lg font-bold transition-all shadow-md border-2 ${
                filter === 'all'
                  ? 'bg-neon-blue text-white border-neon-blue shadow-[0_0_16px_2px_#1E90FF]'
                  : 'bg-black text-neon-blue border-neon-blue hover:bg-[#231651] hover:text-white'
              }`}
            >
              All ({tickets.length})
            </button>
            <button
              onClick={() => setFilter('upcoming')}
              className={`px-6 py-2 rounded-full text-lg font-bold transition-all shadow-md border-2 ${
                filter === 'upcoming'
                  ? 'bg-neon-blue text-white border-neon-blue shadow-[0_0_16px_2px_#1E90FF]'
                  : 'bg-black text-neon-blue border-neon-blue hover:bg-[#231651] hover:text-white'
              }`}
            >
              Upcoming ({tickets.filter(t => new Date(t.event.date) > new Date()).length})
            </button>
            <button
              onClick={() => setFilter('past')}
              className={`px-6 py-2 rounded-full text-lg font-bold transition-all shadow-md border-2 ${
                filter === 'past'
                  ? 'bg-neon-blue text-white border-neon-blue shadow-[0_0_16px_2px_#1E90FF]'
                  : 'bg-black text-neon-blue border-neon-blue hover:bg-[#231651] hover:text-white'
              }`}
            >
              Past ({tickets.filter(t => new Date(t.event.date) <= new Date()).length})
            </button>
          </div>
        </div>

        {/* Tickets Grid */}
        {filteredTickets.length === 0 ? (
          <div className="text-center py-20">
            <TicketIcon className="mx-auto h-12 w-12 text-neon-pink" />
            <h3 className="mt-4 text-2xl font-bold text-neon-pink">No tickets found</h3>
            <p className="mt-2 text-lg text-white">
              {filter === 'all' 
                ? "You haven't purchased any tickets yet."
                : `You don't have any ${filter} tickets.`
              }
            </p>
            <div className="mt-8">
              <Link
                to="/events"
                className="inline-flex items-center px-6 py-3 rounded-full text-lg font-bold text-white bg-neon-blue hover:bg-neon-pink hover:shadow-[0_0_16px_2px_#FF1EC6] transition-all"
              >
                Browse Events
              </Link>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredTickets.map((ticket) => (
              <div
                key={ticket.id}
                className="bg-black rounded-2xl shadow-lg overflow-hidden hover:scale-105 hover:shadow-[0_0_24px_4px_#1E90FF,0_0_32px_8px_#FF1EC6] transition-transform duration-200 flex flex-col"
              >
                {ticket.event.image && (
                  <img
                    src={ticket.event.image}
                    alt={ticket.event.title}
                    className="w-full h-52 object-cover border-b-4 border-transparent hover:border-neon-pink transition-all duration-200"
                  />
                )}
                <div className="p-6 flex flex-col gap-2 flex-1">
                  <div className="flex items-center justify-between mb-2">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-bold bg-[#1E90FF22] text-neon-blue uppercase tracking-wide">
                      {ticket.event.category}
                    </span>
                    <span className="text-sm text-neon-pink font-bold">
                      {getDisplayStatus(ticket.status)}
                    </span>
                  </div>
                  <h3 className="text-2xl font-extrabold text-white mb-2 truncate">
                    {ticket.event.title}
                  </h3>
                  <div className="flex items-center gap-2 text-neon-blue font-bold">
                    <CalendarIcon className="w-5 h-5" />
                    <span>{format(new Date(ticket.event.date), 'PPP')}</span>
                  </div>
                  <div className="flex items-center gap-2 text-white font-semibold">
                    <MapPinIcon className="w-5 h-5 text-neon-pink" />
                    <span>{ticket.event.venue}</span>
                  </div>
                  <div className="flex items-center gap-2 mt-2">
                    <TicketIcon className="w-5 h-5 text-neon-pink" />
                    <span className="text-neon-pink font-bold text-lg">
                      ₹{ticket.price}
                    </span>
                  </div>
                  <div className="flex items-center gap-2 mt-2">
                    <span className="text-white text-sm font-semibold">
                      Purchased: {format(new Date(ticket.purchasedAt), 'PPP p')}
                    </span>
                  </div>
                  {/* Action Buttons */}
                  <div className="flex flex-col gap-3 mt-4">
                    {/* Sell Ticket Button - More Prominent */}
                    <button
                      onClick={() => openResellModal(ticket)}
                      className="w-full inline-flex items-center justify-center px-4 py-3 rounded-full text-sm font-bold text-white bg-neon-pink hover:bg-neon-blue hover:shadow-[0_0_16px_2px_#1E90FF] transition-all border-2 border-neon-pink hover:border-neon-blue"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                      </svg>
                      Sell Ticket
                    </button>

                    {/* QR Code Button */}
                    {ticket.qrCode && isEventWithin24Hours(ticket.event.date) ? (
                      <a
                        href={ticket.qrCode}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center justify-center px-4 py-2 rounded-full text-sm font-bold text-white bg-neon-blue hover:bg-neon-pink hover:shadow-[0_0_16px_2px_#FF1EC6] transition-all border-2 border-neon-blue hover:border-neon-pink"
                      >
                        <QrCodeIcon className="w-5 h-5 mr-2" /> Show QR Code
                      </a>
                    ) : (
                      <span className="text-center text-neon-blue text-xs font-semibold px-4 py-2">
                        {getQRCodeMessage(ticket.event.date)}
                      </span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Resell Modal */}
        <Transition show={resellModalOpen} as={React.Fragment}>
          <Dialog as="div" className="fixed z-50 inset-0 overflow-y-auto" onClose={closeResellModal}>
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={React.Fragment}
                enter="ease-out duration-300" enterFrom="opacity-0" enterTo="opacity-100"
                leave="ease-in duration-200" leaveFrom="opacity-100" leaveTo="opacity-0"
              >
                <Dialog.Overlay className="fixed inset-0 bg-black bg-opacity-80 transition-opacity" />
              </Transition.Child>
              <span className="inline-block align-middle h-screen" aria-hidden="true">&#8203;</span>
              <Transition.Child
                as={React.Fragment}
                enter="ease-out duration-300" enterFrom="opacity-0 scale-95" enterTo="opacity-100 scale-100"
                leave="ease-in duration-200" leaveFrom="opacity-100 scale-100" leaveTo="opacity-0 scale-95"
              >
                <div className="inline-block bg-[#18122B] rounded-2xl p-8 shadow-2xl max-w-md w-full align-middle relative z-10" onClick={(e) => e.stopPropagation()}>
                  <Dialog.Title className="text-2xl font-extrabold text-neon-pink mb-4">Sell Your Ticket</Dialog.Title>
                  <p className="text-white text-sm mb-6">Choose how you want to sell your ticket and set your price.</p>
                  <form onSubmit={handleResell} className="space-y-6">
                    <div>
                      <label className="block text-sm font-bold text-white mb-2">Selling Price (₹)</label>
                      <input
                        type="number"
                        min="1"
                        value={resellPrice}
                        onChange={e => setResellPrice(e.target.value)}
                        className="block w-full px-4 py-3 bg-[#231651] text-white border border-[#23223a] rounded-lg focus:outline-none focus:ring-2 focus:ring-neon-pink font-semibold placeholder-white/60"
                        placeholder="Enter your selling price"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-bold text-white mb-2">Selling Method</label>
                      <div className="space-y-3">
                        <div 
                          className={`flex items-center p-3 bg-[#231651] rounded-lg border cursor-pointer transition-all ${
                            resellType === 'DIRECT_SALE' 
                              ? 'border-neon-pink bg-[#231651]' 
                              : 'border-[#23223a] hover:border-neon-pink'
                          }`}
                          onClick={(e) => {
                            e.stopPropagation();
                            setResellType('DIRECT_SALE');
                          }}
                        >
                          <div className={`w-4 h-4 rounded-full border-2 mr-3 flex items-center justify-center ${
                            resellType === 'DIRECT_SALE' 
                              ? 'border-neon-pink' 
                              : 'border-gray-400'
                          }`}>
                            {resellType === 'DIRECT_SALE' && (
                              <div className="w-2 h-2 rounded-full bg-neon-pink"></div>
                            )}
                          </div>
                          <div>
                            <div className="font-bold text-white">Direct Sale</div>
                            <div className="text-sm text-gray-300">Sell immediately at your set price</div>
                          </div>
                        </div>
                        <div 
                          className={`flex items-center p-3 bg-[#231651] rounded-lg border cursor-pointer transition-all ${
                            resellType === 'AUCTION' 
                              ? 'border-neon-pink bg-[#231651]' 
                              : 'border-[#23223a] hover:border-neon-pink'
                          }`}
                          onClick={(e) => {
                            e.stopPropagation();
                            setResellType('AUCTION');
                          }}
                        >
                          <div className={`w-4 h-4 rounded-full border-2 mr-3 flex items-center justify-center ${
                            resellType === 'AUCTION' 
                              ? 'border-neon-pink' 
                              : 'border-gray-400'
                          }`}>
                            {resellType === 'AUCTION' && (
                              <div className="w-2 h-2 rounded-full bg-neon-pink"></div>
                            )}
                          </div>
                          <div>
                            <div className="font-bold text-white">Auction</div>
                            <div className="text-sm text-gray-300">Let buyers bid on your ticket</div>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="flex justify-end gap-4 mt-6">
                      <button
                        type="button"
                        onClick={closeResellModal}
                        className="px-6 py-2 rounded-full text-lg font-bold text-neon-pink border border-neon-pink bg-black hover:bg-[#231651] hover:text-white focus:outline-none focus:ring-2 focus:ring-neon-pink transition-all"
                      >
                        Cancel
                      </button>
                      <button
                        type="submit"
                        disabled={resellLoading}
                        className="px-6 py-2 rounded-full text-lg font-bold text-white bg-neon-blue hover:bg-neon-pink hover:shadow-[0_0_16px_2px_#FF1EC6] focus:outline-none focus:ring-2 focus:ring-neon-blue disabled:opacity-50 disabled:cursor-not-allowed transition-all"
                      >
                        {resellLoading ? 'Listing...' : `List for ${resellType === 'AUCTION' ? 'Auction' : 'Sale'}`}
                      </button>
                    </div>
                  </form>
                </div>
              </Transition.Child>
            </div>
          </Dialog>
        </Transition>
      </div>
    </div>
  );
};

export default MyTicketsPage;
