import React from 'react';
import { Link } from 'react-router-dom';

const events = [
  {
    name: 'Sunburn Goa 2025',
    date: 'Dec 27, 2025',
    price: '₹2,499',
    image: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=600&q=80',
    dateColor: 'text-neon-blue',
  },
  {
    name: 'Arijit Singh Live',
    date: 'Nov 15, 2025',
    price: '₹3,999',
    image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=600&q=80',
    dateColor: 'text-[#3EBEFF]', // lighter blue for variation
  },
  {
    name: 'NH7 Weekender',
    date: 'Oct 10, 2025',
    price: '₹1,799',
    image: 'https://images.unsplash.com/photo-1511671782779-c97d3d27a1d4?auto=format&fit=crop&w=600&q=80',
    dateColor: 'text-neon-blue',
  },
];

const HomePage: React.FC = () => {
  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-darkpurple to-darkpurple-light font-sans flex flex-col">
      {/* Navbar */}
      <nav className="w-full bg-blackish rounded-b-2xl px-6 py-4 flex items-center justify-between shadow-lg mb-8">
        <div className="flex items-center gap-3">
          <span className="text-white font-extrabold text-xl tracking-widest font-sans uppercase">CONCERT TICKET RESALE</span>
          <svg className="w-7 h-7 text-neon-pink" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
            <path d="M4 7V5a2 2 0 012-2h12a2 2 0 012 2v2m0 10v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2m16-10H4m16 0v10a2 2 0 01-2 2H6a2 2 0 01-2-2V7" />
          </svg>
        </div>
        <div className="flex gap-6">
          <Link to="/register" className="text-neon-pink font-bold text-lg hover:text-neon-blue hover:underline transition-all">Sign Up</Link>
          <Link to="/login" className="text-neon-pink font-bold text-lg hover:text-neon-blue hover:underline transition-all">Sign In</Link>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center py-16 px-4">
        <h1 className="text-neon-pink font-extrabold uppercase text-5xl md:text-7xl font-sans mb-6 tracking-tight drop-shadow-neon">BUY & SELL<br />CONCERT TICKETS</h1>
        <p className="text-white text-xl md:text-2xl mb-10 font-medium">Find great deals on resale tickets for live shows.</p>
        <Link to="/events" className="bg-neon-blue text-white font-bold text-xl px-10 py-4 rounded-full shadow-neon transition-all hover:bg-neon-pink hover:shadow-pink-500/40 hover:scale-105 uppercase">GET STARTED</Link>
      </section>

      {/* Popular Events Section */}
      <section className="w-full max-w-5xl mx-auto mt-12 px-4">
        <div className="bg-blackish rounded-3xl p-8 shadow-lg">
          <h2 className="text-white font-extrabold text-3xl mb-8 uppercase tracking-wide">POPULAR EVENTS</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {events.map((event, idx) => (
              <div
                key={event.name}
                className="bg-blackish rounded-2xl overflow-hidden shadow-neon transform transition-transform hover:scale-105 hover:shadow-neon flex flex-col"
              >
                <img src={event.image} alt={event.name} className="w-full h-48 object-cover" />
                <div className="p-5 flex flex-col gap-2 flex-1">
                  <span className="text-white font-bold text-xl">{event.name}</span>
                  <span className={`${event.dateColor} font-semibold`}>{event.date}</span>
                  <span className="text-neon-pink font-bold text-lg">{event.price}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
