import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar: React.FC = () => {
  const location = useLocation();
  const isActive = (href: string) => location.pathname === href;

  return (
    <nav className="w-full bg-white shadow-sm py-3 px-6 flex items-center justify-between">
      <div className="flex items-center space-x-8">
        <Link to="/" className="flex items-center space-x-2">
          <span className="w-7 h-7 rounded-lg bg-[#D6A77A] flex items-center justify-center font-extrabold text-white text-lg">L</span>
          <span className="font-extrabold text-xl text-[#222] tracking-tight">Fastpass</span>
        </Link>
        <div className="hidden md:flex items-center space-x-6 ml-8">
          <Link to="/buy" className={`text-[#222] font-semibold hover:text-[#D6A77A]${isActive('/buy') ? ' underline' : ''}`}>Buy</Link>
          <Link to="/sell" className={`text-[#222] font-semibold hover:text-[#D6A77A]${isActive('/sell') ? ' underline' : ''}`}>Sell</Link>
          <Link to="/auction" className={`text-[#222] font-semibold hover:text-[#D6A77A]${isActive('/auction') ? ' underline' : ''}`}>Auction</Link>
        </div>
      </div>
      <div className="flex-1 flex justify-center">
        <input
          type="text"
          placeholder="Search eventsâ€¦"
          className="w-full max-w-xs px-4 py-2 rounded-full border border-[#E5E5E5] bg-[#FAF8F6] text-[#222] placeholder-[#A9A9A9] focus:outline-none focus:ring-2 focus:ring-[#D6A77A]"
        />
      </div>
      <div className="flex items-center space-x-3">
        <Link to="/signup" className="px-5 py-2 rounded-full font-bold bg-[#D6A77A] text-white shadow hover:bg-[#b98a5e] transition">Sign Up</Link>
        <Link to="/login" className="px-5 py-2 rounded-full font-bold bg-white border border-[#E5E5E5] text-[#222] hover:bg-[#F5E7D6] transition">Log In</Link>
      </div>
    </nav>
  );
};

export default Navbar;
